jQuery(document).ready(function($){ $(".pageloader").remove(); });
jQuery(document).ready(function($){
  $(".image-grid .image").click(function(e){
    e.preventDefault();
    var gallery_imgsrc = $("img", this).attr("src");
    $("#gallery_modal .modal-body").html('<button type="button" class="close" data-dismiss="modal">X</button><img src="'+gallery_imgsrc+'">');
    $('#gallery_modal').modal('toggle');
  });
});
